def find_kth_smallest(list1, list2, k):
    # Ensure that list1 is the smaller list to improve efficiency
    if len(list1) > len(list2):
        return find_kth_smallest(list2, list1, k)

    # If the smaller list is empty, return the kth element from the second list
    if not list1:
        return list2[k - 1]

    # If k is 1, return the minimum of the first elements from both lists
    if k == 1:
        return min(list1[0], list2[0])

    # Divide k into two parts
    i = min(len(list1), k // 2)
    j = min(len(list2), k - i)

    if list1[i - 1] > list2[j - 1]:
        # If the i-th element of list1 is greater than the j-th element of list2,
        # then the kth smallest cannot be in the first j elements of list2.
        # We reduce k by j and discard the first j elements from list2.
        return find_kth_smallest(list1, list2[j:], k - j)
    else:
        # If the i-th element of list1 is less than or equal to the j-th element of list2,
        # then the kth smallest cannot be in the first i elements of list1.
        # We reduce k by i and discard the first i elements from list1.
        return find_kth_smallest(list1[i:], list2, k - i)

# Example usage:
